mapper.select:
SELECT , UI.UI_ID FROM COMMUNITY_BOARD
WHERE CB_NUM > 0
ORDER BY CB_NUM DESC LIMIT #{skip}, #{amount}

mapper.update:
UPDATE COMMUNITY_BOARD
SET CB_VIEW_CNT = CB_VIEW_CNT + 1
WHERE CB_NUM = #{cbNum}

UPDATE COMMUNITY_BOARD
SET CB_COMMENT_CNT = CB_COMMENT_CNT + #{amount}
WHERE CB_NUM = #{cbNum}
