mapper.insert:
INSERT INTO MOUNTAIN_INFO (
) VALUES
( #{mountainInfo.mntncd}, #{mountainInfo.mntnm}, #{mountainInfo.mntheight}, #{mountainInfo.areanm}, #{mountainInfo.aeatreason}, #{mountainInfo.etccourse}, #{mountainInfo.overview}, #{mountainInfo.subnm}, #{mountainInfo.tourisminf}, #{mountainInfo.details}, DATE_FORMAT(NOW(),'%Y%m%d'), DATE_FORMAT(NOW(),'%Y%m%d') #{mountainInfo.mntnattchimageseq}, #{mountainInfo.transport}, #{mountainInfo.lat}, #{mountainInfo.lot} )

update.foreach:
UPDATE MOUNTAIN_INFO
MNTHEIGHT=#{mountainInfo.mntheight}, AREANM=#{mountainInfo.areanm}, AEATREASON=#{mountainInfo.aeatreason}, ETCCOURSE=#{mountainInfo.etccourse}, OVERVIEW=#{mountainInfo.overview}, SUBNM=#{mountainInfo.subnm}, TOURISMINF=#{mountainInfo.tourisminf}, DETAILS=#{mountainInfo.details}, MI_LMODAT=DATE_FORMAT(NOW(),'%Y%m%d') MNTNATTCHIMAGESEQ=#{mountainInfo.mntnattchimageseq}, TRANSPORT=#{mountainInfo.transport}, LAT=#{mountainInfo.lat}, LOT=#{mountainInfo.lot}
AND MNTNM=#{mountainInfo.mntnm}

mapper.update:
UPDATE
	MOUNTAIN_INFO

	MNTHEIGHT=#{mntheight}, AREANM=#{areanm}, AEATREASON=#{aeatreason}, 
	ETCCOURSE=#{etccourse}, OVERVIEW=#{overview}, SUBNM=#{subnm}, 
