mapper.select:
SELECT UI_NUM
FROM PARTY_INFO
WHERE UI_NUM = #{loginUiNum} AND PI_NUM = #{piNum}

mapper.insert:
INSERT INTO PARTY_INFO
(

MI_NUM, UI_ID
)
VALUES(
#{piName}, #{piExpdat}, #{piMeetingTime},
#{piMemberCnt}, #{piProfile},
#{miNum}, #{uiId}
)

mapper.update:
UPDATE PARTY_INFO
SET PI_ACTIVE = 0
WHERE PI_NUM = #{piNum}
